:root {
    /* type */
    --primaryFont: 'roboto condensed';
    --headFont: 'roboto black';
    --pBodyTxt: 32px;
    /* colors */
    --primaryBlack: rgb(20, 18, 18);
    --secondaryBlack: rgba(114, 112, 112, 0.664);
    --primaryHighllight: #ffffff;
    /* misc */
    --sub-text: rgba(88, 87, 87, 0.734);
    --bwFilter: grayscale(1) contrast(150%);
    --fadeBorder: 1px solid rgba(208, 208, 208, 0.268);
    /* testing */
    --textColor: papayawhip;
}

* {
    box-sizing: border-box;
    text-decoration: none;
    font-family: var(--primaryFont);
    transition: all 1s ease-in-out;
}

html,
body {
    margin: 0;
    padding: 0;
}

header {
    position: fixed;
    top: 0;
    z-index: -1;
    width: 100%;
    height: 100vh;
}

section {
    position: absolute;
    top: 100vh;
    z-index: 9990;
    width: 100%;
    height: 100vh;
    background: var(--textColor);
    display: flex;
    flex-direction: column;
}

section:first-of-type {
    top: 100vh;
}

nav {
    top: 0;
    position: fixed;
    width: 100%;
    height: 74px;
    margin-top: 1em;
    padding: 2em;
    display: flex;
    justify-content: space-between;
    z-index: 9999;
}

.nav-item {
    padding: 2px;
}

.nav-item {
    color: var(--primaryBlack);
    text-transform: uppercase;
    font-family: 'roboto condensed';
    line-height: 4px;
}

.col-2 {
    top: 0;
    position: absolute;
    width: 100%;
    height: 100vh;
    z-index: 9990;
    display: flex;
    background-size: cover;
    object-fit: cover;
}

.sect {
    flex: 1;
}

.sect-1 {
    background: #fff;
}

.sect-2 {
    background: url(1de964a5b15cbcca52b2fd1130b52d43.jpg) center no-repeat;
    object-fit: cover;
    background-size: cover;
    filter: var(--bwFilter);
}

.nav-item {
    flex-basis: 100px;
    display: flex;
    align-self: center;
}

.logo {
    background: white;
    height: inherit;
    border: 4px solid black;
    display: flex;
}

/* TODO: change to svg background */

.logo span {
    font-size: 1.8em;
    font-family: 'F37BellaStencil';
    margin: auto;
    text-transform: lowercase;
}

.social-links {
    justify-content: space-between;
}

.menu {
    font-weight: 700;
    letter-spacing: -1px;
}

.nav-item i {
    width: 24px;
    height: 24px;
    background: black;
    cursor: pointer;
    border: 2px solid white;
}

i {
    width: 2px;
    height: 60px;
    background: #000;
    cursor: pointer;
}

