.h1,
h2,
h3 {
    font-family: 'roboto black'
}

.hidden {
    display: none;
}

.sect {
    padding: 8% 4em 1em 10em;
    display: flex;
}

.inner-con {
    flex-basis: 100%;
}

.hor-list {
    margin: auto;
    margin-left: 0;
}

.summon {
    margin: auto;
    display: flex;
    flex-direction: column;
}

.summon li {
    margin-bottom: 1em;
    padding: 1em;
    list-style: none;
    font-size: 1.5em;
}

.summon li a {
    color: var(--secondaryBlack);
    transform: translateX(1em);
}

.summon .active {
    border-bottom: var(--fadeBorder);
    border-top: var(--fadeBorder);
}

.summon li.active a {
    color: var(--primaryBlack);
}

.summon .active::before {
    content: '';
    position: absolute;
    background: #000;
    width: 10px;
    height: 2em;
    /* transform: translate(-2.69em, 5px); */
    left: 0;
}

.descriptor {
    border: rebeccapurple 1px solid;
    text-align: right;
    color: wheat;
}

.descriptor h1 {
    margin-top: 3em;
}

button,
button.primary {
    outline: none;
    border: none;
    padding: 16px 30px;
    margin-top: 2em;
    width: 200px;
    background: var(--primaryHighllight);
    color: #000;
    border: 2px solid var(--primaryBlack);
    text-transform: uppercase;
    font-weight: 700;
    letter-spacing: 1px;
    cursor: pointer;
}

button.primary {
    color: var(--primaryHighllight);
    background: var(--primaryBlack);
}

