.summon li {
    background-color: transparent;
    margin: 0;
}

.summon li:hover a {
    transform: scale(1.2)
}

.summon li a {
    text-transform: capitalize;
    font-size: 1.5em;
    font-weight: bolder
}

.summon li .sub {
    color: var(--sub-text);
    font-size: 14px;
    margin: 0;
}

.summon li:not(.active) .sub {
    opacity: .5;
}

.line {
    width: 100%;
    height: 2px;
    background: var(--primaryBlack);
    margin: auto;
    margin-top: 0;
}

.backdrop--left {
    margin: auto 0;
    width: 50%;
    height: 100%;
    background: #000;
    z-index: -1;
}

.content--card {
    width: 76%;
    height: 70vh;
    margin: auto 0;
    top: calc(20%);
    position: absolute;
    z-index: 1;
    background: palevioletred;
    right: 0;
    display: flex;
}

.with-text {
    padding-left: 10%;
    padding-top: 10%;
    padding-bottom: 10%;
}

/* responsive */

@media screen and (max-width: 900px) {
    .col-2 {
        flex-direction: column;
    }
    .backdrop--left {
        width: 100%;
    }
}
